mapper.select:
select v,r,re.u,vgrade,vimg1,vimg2,vimg3,vcontent,writedate, uuid from review re ,(select u,uuid from member) me where re.u = me.u AND (ROWNUM > (#{vcurpage}-1*#{vonepage}) AND ROWNUM <= #{vcurpage}*#{vonepage})
and r=#{r}
order by writedate

mapper.update:
update booking set
v = (select v from review where b = #{b} and r = #{r} and u = #{u} a = #{a})
where b = #{b} and r = #{r} and u = #{u} a = #{a}
